import React from 'react';
import Homepage from './Homepage'

function Category(props) {
    const { database } = props;
    return (
        <div>
            <img className="small" src={database.categories.categoryimage} alt={database.categories.categoryname}></img>
            <h3>{database.categories.categoryname}</h3>
            <button>Show Products</button>
        </div>
    )
}

export default Category
