import React from 'react';
import data from '../data';
import {Link} from 'react-router-dom';

export default function Caroussel() {
    const { types } = data;

    document.addEventListener('DOMContentLoaded', () => {
        const slideTime = 500;
        // const previousButton = document.querySelector('#previous');
        // const nextButton = document.querySelector('#next');
        const allSlides = [...document.querySelectorAll(".slide")];

        let clickable = true;
        let active = null;
        let newActive = null;

        function initSlider() {
            allSlides.forEach(slide => {
                slide.setAttribute(
                    'style',
                    `transition: transform ${slideTime}ms ease;
                             animation-duration: ${slideTime}ms;`
                );
            });
        }

        function changeSlide(forward) {
            if (clickable) {
                clickable = false;
                active = document.querySelector(".active");
                const activeSlideIndex = allSlides.indexOf(active);
                if (forward) {
                    // console.log(activeSlideIndex)
                    // console.log(allSlides.length)
                    // console.log((activeSlideIndex + 1) % allSlides.length)

                    newActive = allSlides[(activeSlideIndex + 1) % allSlides.length];
                    active.classList.add('slideOutLeft');
                    newActive.classList.add('slideInRight', 'active');
                } else {
                    newActive = allSlides[(activeSlideIndex - 1 + allSlides.length) % allSlides.length];
                    active.classList.add('slideOutRight');
                    newActive.classList.add('slideInLeft', 'active');
                }
            }
        }
        allSlides.forEach(slide => {
            slide.addEventListener('transitionend', () => {
                if (slide === active && !clickable) {
                    clickable = true;
                    active.className = 'slide';
                }
            });
        });

        //Event Listeners - make buttons clickable
        // nextButton.addEventListener('click', () => {
        //     changeSlide(true);
        // })
        // previousButton.addEventListener('click', () => {
        //     changeSlide(false);
        // })

        //Initslider making it happen
        initSlider();


        return (
            <div>
                <div class="carousel-container">
                    <div class="panel right" onClick={changeSlide(false)}>
                        <button id="next" class="button next">
                            &#10095;
                        </button>
                    </div>
                    <div class="panel left">
                        <button id="previous" class="button previous" onClick={changeSlide(true)}>
                            &#10094;
                        </button>
                    </div>
                    <div class="slide">
                        <div className="feat-prod">
                            <img src={types[0].products[0].image} alt={types[0].products[0].name} />
                            <div className="feat-prod-text">
                                <h3>{types[0].products[0].name}</h3>
                                <p>{types[0].products[0].description}</p>
                                <Link to="/">See product</Link>
                                <button>Add to basket</button>
                            </div>
                        </div>
                    </div>
                    <div class="slide active"><div className="feat-prod"><img src={types[0].products[1].image} alt={types[0].products[1].name} /></div></div>
                    <div class="slide"><div className="feat-prod"><img src={types[2].products[4].image} alt={types[2].products[4].name} /></div></div>
                </div>
            </div>
        )
    })

}
