import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome, faShoppingBasket, faSearch } from '@fortawesome/free-solid-svg-icons';
import { useAuth } from '../contexts/AuthContext';
import { Link } from 'react-router-dom'

function Navbar() {
    const currentUser = useAuth()

    function linkToLogin() {
        if (currentUser === undefined) {
            return(
                <Link to="/login"><li>Log in / Register</li></Link>
            )}
        return <Link to="/"><li>{currentUser.email}</li></Link>
    }


    return (
        <div className="navbar">
                <h2 className="logo-holder">Placeholder.</h2>
                <ul className="nav-links">
                    <li><FontAwesomeIcon icon={faSearch} size="2x" /></li>
                    <Link to="/"><li><FontAwesomeIcon icon={faHome} size="2x" /></li></Link>
                    <Link to="/basket"><li><FontAwesomeIcon icon={faShoppingBasket} size="2x" /></li></Link>
                    <linkToLogin/>
                </ul>
        </div>
    )
}

export default Navbar
