import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome, faShoppingBasket, faSearch } from '@fortawesome/free-solid-svg-icons';
import { useAuth } from '../contexts/AuthContext';

function Navbar() {
    const  currentUser  = useAuth()
    return (
        <div className="navbar">
            <h2 className="logo-holder">Placeholder.</h2>
            <ul className="nav-links">
                <li><FontAwesomeIcon icon={faSearch} size="2x"/></li>
                <li><FontAwesomeIcon icon={faHome} size="2x"/></li>
                <li><FontAwesomeIcon icon={faShoppingBasket} size="2x"/></li>
                <li>{`${currentUser === undefined ? "Log In / Register" : `Hello ${currentUser.user.firstNameRef}`}`}</li>
            </ul>
        </div>
    )
}

export default Navbar
