export default function Basket(props) {
    const { basketItems, onAdd, onRemove } = props;
    const itemsPrice = cartItems.reduce((a, c) => a+ c.price_number *qty)
    return (
        <div>
            <h2>Basket Items</h2>
            <div>
                <div>{basketItems.length === 0 && <div>Basket is empty.</div>}</div>
                {basketItems.map((item) => (
                    <div key={item.id}>
                        <div>{item.name}</div>
                        <div>
                            <button onClick={() => onAdd(item)}>+</button>
                            <button onClick={() => onRemove(item)}>-</button>
                        </div>
                        <div>
                            {item.qty} x {item.price_number.toFixed(2)}
                        </div>
                    </div>
                ))}
            </div>

        </div>
    )
}
