import React, { useRef, useState } from 'react'
import { Card, Form, Button, Alert } from 'react-bootstrap'
import { useAuth } from '../contexts/AuthContext'
import { Link, useHistory } from 'react-router-dom'

function LogIn() {
    const emailRef = useRef();
    const { login } = useAuth();
    const [error, setError] = useState("")
    const [loading, setLoading] = useState(false);
    const history = useHistory();

    async function handleSubmit(e) {
        e.preventDefault()
        try {
            setError("")
            setLoading(true)
            // await login(emailRef.current.value, passwordRef.current.value)
            history.push('/')
        } catch {
            setError('Failed to sign in')
        }
        setLoading(false)
    }


    return (
        <>
            <Card>
                <Card.Body>
                    <h2 className="text-center mb-4">Reset Password</h2>
                    {error && <Alert variant="danger">{error}</Alert>}
                    <Form onSubmit={handleSubmit}>
                        <Form.Group id="email">
                            <Form.Label>Email</Form.Label>
                            <Form.Control type="email" ref={emailRef} required />
                        </Form.Group>
                        <Button disabled={loading} className="w-100 mt-4 text-center" type="submit">Reset Password</Button>
                        <div className="w-100 mt-3 text-center">
                            <Link to="/forgot-password">Log in</Link>
                        </div>
                    </Form>
                </Card.Body>
            </Card>
            <div>
                <p className="text-center">Don't have an account yet? <Link to="/signup">Register here!</Link></p>
            </div>
        </>
    )
}

export default LogIn
