import React from 'react';
import data from '../data';


export default function ProductOverview({ match }) {
    const { types } = data;
    console.log(match.params.type_name)
    let filteredArray;
    let filteredCategory
    for (let index = 0; index < types.length; index++) {
        category = types[index];
        if (category.type_name === match.params.type_name) {
            filteredArray = category.products;
            filteredCategory = category;
        }
    }
    console.log(filteredArray)
    console.log(filteredCategory)

    return (
        <div>
            <div className="categflex">
                <img src={category.type_image} alt={category.type_name} />
                <div className="categoryInfo"></div>

            </div>
            <div className="productOverview">
                {filteredArray.map((product)=>
                    <div>{product.name}</div>
                )}
            </div>
        </div>
    )

}
