import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome, faShoppingBasket, faSearch } from '@fortawesome/free-solid-svg-icons';
import { useAuth } from '../contexts/AuthContext';
import { Link, useHistory } from 'react-router-dom'


function Navbar() {
    const [error, setError] = useState();
    const { currentUser, logout } = useAuth()
    const history = useHistory();

    async function handleLogout(){
        setError('')

        try{
            await logout()
            history.push('/login')
        } catch {
            setError('Failed to logout')
        }
    }

    return (
        <div className="navbar">
                <Link to="/"><h2 className="logo-holder">Placeholder.</h2></Link>
                <ul className="nav-links">
                    <Link><li><FontAwesomeIcon icon={faSearch} size="2x" /></li></Link>
                    <Link to="/"><li><FontAwesomeIcon icon={faHome} size="2x" /></li></Link>
                    <Link to="/basket"><li><FontAwesomeIcon icon={faShoppingBasket} size="2x" /></li></Link>
                    {currentUser === (null && null) ? <Link to="/login"><li>Log in / Register</li></Link> : <Link to="/"><li onClick={handleLogout}>Logout</li></Link>}
                </ul>
                {error}
        </div>
    )
}

export default Navbar
