import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome, faShoppingBasket, faSearch } from '@fortawesome/free-solid-svg-icons';
import { useAuth } from '../contexts/AuthContext';
import { Link, useHistory } from 'react-router-dom';

function Navbar() {
    const [error, setError] = useState();
    const { currentUser, logout } = useAuth()
    const history = useHistory();

    async function handleLogout(){
        setError('')

        try{
            await logout()
            history.push('/login')
        } catch {
            setError('Failed to logout')
        }
    }

    //Burger Toggle Mobile
    function NavSlide(){
        const burger = document.querySelector('.burger');
        const nav = document.querySelector('.nav-links');
        const [toggled, setToggled] = useState(null);
        const navSlide = () => {
            if (toggled = null){
                return setToggled(null)
            }
            setToggled(nav.classList.toggle('nav-active'), burger.classList.toggle('toggle'))
        }




    //     burger('click', () => {
    //         nav.classList.toggle('nav-active');

    //         //burger to cross
    //         burger.classList.toggle('toggle');
    //     })
    // }
    }

    return (
        <nav className="navbar">
                <Link to="/" ><h2 className="logo-holder">Placeholder.</h2></Link>
                <ul className="nav-links">
                    <Link to="/"><li><FontAwesomeIcon icon={faSearch} size="2x" /></li></Link>
                    <Link to="/" ><li><FontAwesomeIcon icon={faHome} size="2x" /></li></Link>
                    <Link to="/basket" ><li><FontAwesomeIcon icon={faShoppingBasket} size="2x" /></li></Link>
                    {currentUser === (null && undefined) ? <Link to="/login" ><li>Log in / Register</li></Link> : <Link to="/"><li onClick={handleLogout}>Logout</li></Link>}
                </ul>
                <div className="burger" onClick={navSlide()}>
                    <div className="line1"></div>
                    <div className="line2"></div>
                    <div className="line3"></div>
                </div>
                {error}
        </nav>
    )
}

export default Navbar
