import React from 'react';
import data from '../data';

export default function ProductDetail({match}) {
    console.log(match)
    const { types } = data;
    console.log(match.params.type_name)
    console.log(match.params.product_name)
    //product array based on right category
    let filteredArray;
    //category base on match
    let filteredCategory;
    //all categories array
    let category;

    let relevantProductArray;

    let relevantProduct;
    for (let index = 0; index < types.length; index++) {
        category = types[index];
        if (category.type_name === match.params.type_name) {
            filteredArray = category.products;
            filteredCategory = category;
        }
    }
    for (let index = 0; index < filteredArray.length; index++) {
        relevantProductArray = filteredArray[index];
        if (relevantProductArray.name === match.params.product_name) {
            relevantProduct.name = relevantProductArray.name;
            relevantProduct.description = relevantProductArray.description;
            relevantProduct.image = relevantProductArray.image;
        }

    }

    console.log(filteredArray.name)
    return (
        <div>
            {filteredArray.name}
        </div>
    )
}
